mapper.update:
UPDATE example SET
	example_idx = #{exampleIdx}
	, example_title = #{exampleTitle}
	, example_ox = #{exampleOx}
WHERE example_no = #{exampleNo}

mapper.insert:
INSERT INTO example(
	question_no
	, example_idx
	, example_title
	, example_ox
) VALUES (
	#{questionNo}
	, #{exampleIdx}
	, #{exampleTitle}
	, #{exampleOx}
)
