mapper.update:
UPDATE example SET
	example_idx = #{exampleIdx}
	, example_title = #{exampleTitle}
	, example_ox = #{exampleOx}
WHERE example_no = #{exampleNo}

mapper.insert:
INSERT INTO example(
	question_no
	, example_idx
	, example_title
	, example_ox
) VALUES (
	#{questionNo}
	, #{exampleIdx}
	, #{exampleTitle}
	, #{exampleOx}
)

mapper.select:
SELECT q.question_no questionNo , q.question_idx questionIdx , q.question_title questionTitle , e.example_no exampleNo , e.example_idx exampleIdx , e.example_title exampleTitle , e.example_ox exampleOx FROM example e RIGHT outer JOIN question q ON e.question_no = q.question_no
example_title LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY q.question_no ASC, e.example_idx ASC LIMIT #{beginRow}, #{rowPerPage}
