mapper.update:
UPDATE question SET
	question_idx = #{questionIdx}
	, question_title = #{questionTitle}
WHERE question_no = #{questionNo}

mapper.insert:
INSERT INTO question(
	test_no
	, question_idx
	, question_title
) VALUES (
	#{testNo}
	, #{questionIdx}
	, #{questionTitle}
)

mapper.select:
SELECT t.test_no testNo , t.test_title testTitle , t.test_date testDate , q.question_no questionNo , q.question_idx questionIdx , q.question_title questionTitle FROM question q RIGHT outer JOIN test t ON q.test_no = t.test_no
question_title LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY t.test_no ASC, q.question_idx ASC LIMIT #{beginRow}, #{rowPerPage}
