mapper.update:
UPDATE test SET
	test_title = #{testTitle}
	, test_date = #{testDate}
WHERE test_no = #{testNo}

mapper.insert:
INSERT INTO test (
	test_title
	, test_date
) VALUES (
	#{testTitle}
	, #{testDate}
)

mapper.select:
SELECT test_no testNo , test_title testTitle , test_date testDate FROM test
test_title LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY test_date ASC LIMIT #{beginRow}, #{rowPerPage}
